insert into author_quotes (author, quote) values ('A.W. Tozer', 'Only an evil desire to shine makes us want to appear other than we are.');
insert into author_quotes (author, quote) values ('G.K. Chesterton', 'Truth, of course, must of necessity be stranger than fiction, for we have made fiction to suit ourselves.');
